const CheckoutPage = () => {
  const { user } = useAuth();
  const [cart, setCart] = useState({ items: [], total: 0 });
  const [shippingAddress, setShippingAddress] = useState('');
  const [processing, setProcessing] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetchCart();
    }
  }, [user]);

  const fetchCart = async () => {
    try {
      const response = await axios.get(`${API}/cart`);
      setCart(response.data);
      if (user.address) {
        setShippingAddress(user.address);
      }
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setLoading(false);
    }
  };

  const initiatePayment = async () => {
    if (!shippingAddress.trim()) {
      alert('Please enter shipping address');
      return;
    }

    setProcessing(true);
    try {
      // Create order
      const orderResponse = await axios.post(`${API}/orders/create`, {
        shipping_address: shippingAddress
      });

      // Initialize Razorpay payment
      const options = {
        key: orderResponse.data.key,
        amount: orderResponse.data.amount,
        currency: orderResponse.data.currency,
        order_id: orderResponse.data.razorpay_order_id,
        name: 'Francium',
        description: 'E-commerce Purchase',
        handler: async function (response) {
          try {
            // Verify payment
            const verifyResponse = await axios.post(`${API}/orders/verify-payment`, {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature
            });

            if (verifyResponse.data.status === 'success') {
              alert('Payment successful! Redirecting to orders...');
              window.location.href = '/orders';
            } else {
              alert('Payment verification failed');
            }
          } catch (error) {
            console.error('Payment verification error:', error);
            alert('Payment verification failed');
          }
        },
        prefill: {
          name: user.full_name,
          email: user.email,
          contact: user.phone || '9999999999'
        },
        theme: {
          color: '#2563eb'
        }
      };

      // Note: In a real implementation, you would load Razorpay script
      // For demo purposes, we'll simulate the payment flow
      alert('Demo Mode: Payment would be processed via Razorpay here. For now, simulating successful payment...');

      // Simulate successful payment for demo
      setTimeout(async () => {
        try {
          const mockPaymentData = {
            razorpay_order_id: orderResponse.data.razorpay_order_id,
            razorpay_payment_id: 'pay_demo_' + Date.now(),
            razorpay_signature: 'demo_signature'
          };

          const verifyResponse = await axios.post(`${API}/orders/verify-payment`, mockPaymentData);
          if (verifyResponse.data.status === 'success') {
            alert('Demo payment successful! Redirecting to orders...');
            window.location.href = '/orders';
          }
        } catch (error) {
          console.error('Demo payment error:', error);
          alert('Demo payment failed');
        }
      }, 2000);

    } catch (error) {
      console.error('Payment initiation error:', error);
      alert('Failed to initiate payment');
    } finally {
      setProcessing(false);
    }
  };

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Please login to checkout</h2>
          <a href="/login" className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
            Login
          </a>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <p className="mt-2">Loading checkout...</p>
        </div>
      </div>
    );
  }

  if (cart.items.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Your cart is empty</h2>
          <a href="/products" className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
            Shop Now
          </a>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8">Checkout</h1>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Shipping Information</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Full Name</label>
                  <input
                    type="text"
