// Add a custom context and hook for cart refresh
const CartRefreshContext = createContext();
const CartRefreshProvider = ({ children }) => {
  const [cartRefreshKey, setCartRefreshKey] = useState(0);
  const refreshCart = () => setCartRefreshKey(k => k + 1);
  return (
    <CartRefreshContext.Provider value={{ cartRefreshKey, refreshCart }}>
      {children}
    </CartRefreshContext.Provider>
  );
};
const useCartRefresh = () => useContext(CartRefreshContext);

// Main App Component with Enhanced Routing
function App() {
  const [currentPath, setCurrentPath] = useState(window.location.pathname);

  useEffect(() => {
    const handlePopState = () => {
      setCurrentPath(window.location.pathname);
    };

    window.addEventListener('popstate', handlePopState);
    return () => window.removeEventListener('popstate', handlePopState);
  }, []);

  const renderPage = () => {
    switch (currentPath) {
      case '/':
        return <HomePage />;
      case '/products':
        return <ProductsPage />;
      case '/login':
        return <LoginPage />;
      case '/register':
        return <RegisterPage />;
      case '/cart':
        return <CartPage />;
      case '/checkout':
        return <CheckoutPage />;
      case '/orders':
        return <OrdersPage />;
      case '/admin':
        return <AdminPage />;
      default:
        return <HomePage />;
    }
  };

  return (
    <CartRefreshProvider>
      <AuthProvider>
        <div className="App">
          <Header />
          {renderPage()}
        </div>
      </AuthProvider>
    </CartRefreshProvider>
  );
}

export default App;
